mapper.update:
UPDATE BOARD 
SET DELETED = true
WHERE BOARDNO = #{ boardNo }

UPDATE BOARD 
SET READCOUNT = READCOUNT + 1
WHERE BOARDNO = #{ boardNo }

UPDATE BOARD 
SET TITLE = #{title}, CONTENT = #{content}
WHERE BOARDNO = #{ boardNo }

mapper.select:
SELECT ATTACHNO, BOARDNO, SAVEDFILENAME, USERFILENAME
FROM BOARDATTACH
WHERE BOARDNO = #{ BOARDNO }

SELECT ATTACHNO, BOARDNO, SAVEDFILENAME, USERFILENAME
FROM BOARDATTACH
WHERE ATTACHNO = #{ attachNo }
